function showFoto(){document.querySelector(".foto-block").innerHTML=`<img src="${data.photo}" alt="${data.first_name} ${data.last_name}" class="img-thumbnail rounded-circle mt-3 mb-3"/>`}function showSkills(){let e="";data.skills.forEach((t=>{e+=`<li>${t}</li>`})),document.querySelector(".skills-list").innerHTML=e,document.querySelector(".address").innerHTML=`<p>${data.address}</p>`}function showContact(){let e=document.querySelector(".contacts-block");e.innerHTML+=`<li><a href="tel:${data.phone}">${data.phone}</a></li>`,e.innerHTML+=`<li><a href="mailto:${data.email}">${data.email}</a></li>`,e.innerHTML+=`<li><a href="callto:${data.skype}">${data.skype}</a></li>`,e.innerHTML+=`<li><a href="${data.linkedin}">${data.linkedin}</a></li>`}function showEducation(){let e="";data.education.forEach((t=>{e+=`<li>${t[0]}, ${t[1]}</li>`})),document.querySelector(".education-list").innerHTML=e}function showAchievements(){let e="";data.achievements.forEach((t=>{e+=`<li>${t},</li>`})),document.querySelector(".achievements-list").innerHTML=e}function showNameSurJob(){document.querySelector(".my-name").innerHTML=`${data.first_name} <span class="color-grey">${data.last_name}</span>`,document.querySelector(".job-title").textContent=data.job_title,document.querySelector(".profile-data").textContent=data.profile}function showExperience(){let e="";experience.forEach((t=>{e+='<section class="mb-5">',e+=` <h4 class="company-name mb-2">${t.company} - ${t.city}, <i>${t.from} - ${t.to}</i></h4>`,e+=`<h5 class="mb-3 experience color-grey">${t.position}</h5>`,e+=`<p>${t.description}</p>`,e+="</section>"})),document.querySelector(".experience-list").innerHTML=e}showFoto(),showSkills(),showContact(),showEducation(),showNameSurJob(),showAchievements(),showExperience();